import React, { Component } from 'react';
import MainCard from '../functional_components/MainCard';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import '../App.css';
import {
	withRouter
} from 'react-router-dom';
import api from '../apis';

class Login extends Component {

    constructor(props){
        super(props);

        this.state = {
           email: '',
           password: ''
        };

        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleSubmit = (e) => {
        e.preventDefault();

        const url = '/findUser'

        const req = {
            email: this.state.email, 
            password: this.state.password
        };

        api({
            method: 'post',
            url: url,
            data: req
        }).then((res) => {
            console.log(res.data);
        })
    } 

    render(){
        return(
            <MainCard> 
                <h4> Monthly Expense Tracker </h4>
                <div>
                    <form autoComplete="off">
                        <TextField id="username" label="email" onChange={e => this.setState({email: e.target.value})}/>
                        <br/>
                        <br/>
                        <TextField id="password" label="password" type="password" onChange={e => this.setState({password: e.target.value})}/>
                        <br/>
                        <br/>
                        <Button variant="outlined">
                            Login
                        </Button>
                    </form>
                    <br/>
                </div>
            </MainCard>
        );
    }
}

export default withRouter(Login);
