import React, { Component } from 'react';
import { PlaidLink } from 'react-plaid-link';
import apis from './apis';
import api from './apis';

class App extends Component {

    constructor(props){
        super(props);


        this.state = {
            linkToken:  ''
        };

        const url = '/LinkToken'

        api.get(url)
        .then(res => {
            this.setState({
                linkToken: res.data.link_token
            });
        })

    }

    // Get Transaction Data using the access token received

    transactionData = (accessToken) => {

        const url = '/TransactionDetails'

        const token = {
            access_token : accessToken
        };

        apis({
            method: 'post',
            url: url,
            data: token
        }).then((res) => {
            console.log(res.data);
        })
    }

    onSuccess = (token, metadata) => {
        console.log(token);

        // Call Token Exchange Endpoint

        const url = '/TokenExchange'

        const publicToken = {
            public_token : token
        };

        apis({
            method: 'post',
            url: url,
            data: publicToken
        }).then((res) => {
            console.log(res.data);

            this.transactionData(res.data.access_token);
        })

        console.log(metadata)
    };

    render() {
        return (
            <div className="App">
                 <PlaidLink
                    token= {this.state.linkToken}
                    onSuccess={this.onSuccess}
                    >
                    Connect a bank account
                </PlaidLink>
            </div>
        );
    }
}
export default App;
