import React, { Component } from 'react';
import MainCard from '../functional_components/MainCard';
import '../App.css';
import apis from '../apis';

class Transactions extends Component {

    constructor(props){
        super(props);

        const tempAccount = this.props.location.state.account;
        const tempToken = this.props.location.state.token;

        this.state = {
            account: tempAccount,
            accessToken: tempToken,
            transactions: []
        };

        console.log(this.props.location);

        this.transactionData();
    }

    transactionData = () => {

        const url = '/AccountTransactionDetails'

        const req = {
            access_token : this.state.accessToken,
            account_ids:  this.state.account.account_id
        };

        apis({
            method: 'post',
            url: url,
            data: req
        }).then((res) => {
            console.log(res.data);
            this.setState({
                transactions: res.data
            });
        })
    }

    getCategoryData = (transaction) => {
        return(
            <div>
                {transaction.category.map((category, index) =>
                    <li key={index}> {category} </li>
                )}
            </div>
        );
    }

    render(){
        return(
            <MainCard> 
                <div id= "transactions">
                    <h4> Expense Tracker </h4>
                    <p>
                        <b> Account Name: </b> {this.state.account.name}
                    </p>
                    <p>
                        <b> Total balance:  </b> ${this.state.account.balances.current}
                    </p>
                    {this.state.transactions.map((transaction, index) =>
                        <div>
                            <li key={index}> {transaction.amount} </li>
                            {this.getCategoryData(transaction)}
                        </div>
                    )}
                </div>
            </MainCard>
        );
    }
}

export default Transactions;
