import React, { Component } from 'react';
import MainCard from '../functional_components/MainCard';
import '../App.css';
import apis from '../apis';
import Accordion from '@material-ui/core/Accordion';
import AccordionSummary from '@material-ui/core/AccordionSummary';
import AccordionDetails from '@material-ui/core/AccordionDetails';
import Typography from '@material-ui/core/Typography';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import AttachMoneyIcon from '@material-ui/icons/AttachMoney';
import Button from '@material-ui/core/Button';
import { withRouter } from 'react-router-dom';

class Transactions extends Component {

    constructor(props){
        super(props);

        var tempAccount = '';
        var tempToken = '';

        if(typeof( this.props.location.state) === "undefined" || this.props.location.state === undefined){
            this.props.history.push('/');
        } else{
            tempAccount = this.props.location.state.account;
            tempToken = this.props.location.state.token;
        }

        this.state = {
            account: tempAccount,
            accessToken: tempToken,
            transactions: [],
            totalAmount: 0
        };

        this.transactionData('/AccountTransactionDetails');
    }

    componentDidCatch(error, info) {
        this.props.history.push('/');
      }

    transactionData = (url) => {
        const req = {
            access_token : this.state.accessToken,
            account_ids:  this.state.account.account_id
        };

        apis({
            method: 'post',
            url: url,
            data: req
        }).then((res) => {
            console.log(res.data);
            this.setState({
                transactions: res.data.transactions,
                totalAmount: res.data.totalAmount
            });
        })
    }

    getCategoryData = (transaction) => {
        return(
            <div>
                <b> Category: </b>
                {transaction.category.map((category, index) =>
                    <span key={index}> {category} </span>
                )}
            </div>
        );
    }


    sortLargeTransaction = () => {
        this.transactionData('/SortedAccountTransactionDetails');
    }

    sortDateTransaction = () => {
        this.transactionData('/AccountTransactionDetails');
    }

    render(){
        return(
            <MainCard> 
                {this.state.account.length > 0 &&
                <div id= "transactions">
                    <h4> Monthly Expense Tracker </h4>
                    <p>
                        <b> Account Name: </b> {this.state.account.name}
                    </p>
                    <p>
                        <b> Total balance:  </b> ${this.state.account.balances.current}
                    </p>
                    <p>
                        <b> Monthly balance:  </b> ${this.state.totalAmount}
                    </p>
                    <Button onClick={() => this.sortLargeTransaction()}>
                        Sort by larget transaction
                    </Button>
                    <Button onClick={() => this.sortDateTransaction()}>
                        Sort by date
                    </Button>
                    {this.state.transactions.map((transaction, index) =>
                        <Accordion key={index}>
                            <AccordionSummary
                                expandIcon={<ExpandMoreIcon />}
                                aria-controls="panel1a-content"
                                id="panel1a-header"
                                >
                                <Typography>
                                <span className="icon">
                                    <AttachMoneyIcon/>
                                </span>
                                    <b className="accountName"> {transaction.name} on {transaction.date}  </b> 
                                </Typography>
                            </AccordionSummary>
                            <AccordionDetails>
                                <div>
                                    <b>Amount:</b>
                                    ${transaction.amount}
                                    <br/>
                                    {this.getCategoryData(transaction)}
                                </div>
                            </AccordionDetails>
                        </Accordion>)}
                </div>}
            </MainCard>
        );
    }
}

export default  withRouter(Transactions);
