    return (
        <div>
            <label className="label" htmlFor="name">
                Enter name:
            </label>
            <input id='name' type ='text'/>
            <button style = 
            {{
                backgroundColor: 'blue', 
                color: 'white'
            }} type="submit"> submit</button>
        </div>
    );
};

// Take the react component and show it on the screen
ReactDOM.render(
    <App />,
    document.querySelector('#root')
);
