import React, { Component } from 'react';
import { PlaidLink } from 'react-plaid-link';
import { withRouter } from 'react-router-dom';
import api from '../apis';
import ListAccounts from '../functional_components/ListAccounts';

class LinkBank extends Component {

    constructor(props){
        super(props);

        if(Object.keys(this.props).length === 0){
            this.props.history.push('/login');
        }

        var email = '';

        if(typeof( this.props.children.location.state) === "undefined"){
            this.props.history.push('/create');
        } else{
            email = this.props.children.location.state.email;
        }

       console.log(email);

        this.state = {
            linkToken:  '',
            accounts: [],
            accessToken: '',
            email: email
        };

        console.log(this.state.email);

        const url = '/LinkToken'

        api.get(url)
        .then(res => {
            this.setState({
                linkToken: res.data.link_token
            });
        })

    }

    // Get Transaction Data using the access token received

    transactionData = (accessToken) => {

        const url = '/TransactionDetails'

        const req = {
            access_token : accessToken,
        };

        api({
            method: 'post',
            url: url,
            data: req
        }).then((res) => {
            console.log(res.data);
            this.setState({
                accounts: res.data
            })
        })
    }

    onSuccess = (token, metadata) => {
        console.log(token);

        // Call Token Exchange Endpoint

        const url = '/TokenExchange'

        const publicToken = {
            public_token : token
        };

        api({
            method: 'post',
            url: url,
            data: publicToken
        }).then((res) => {
            console.log(res.data);

            this.setState({
                accessToken: res.data.access_token
            })

            this.saveAccessToken(this.state.accessToken);

            this.transactionData(res.data.access_token);
        })

        console.log(metadata)
    };

    // api call to save access token to DB for a specific user
    saveAccessToken = (accessToken) => {
        const url = '/updateToken'

        const req = {
            email: this.state.email, 
            access_token: accessToken
        };

        api({
            method: 'post',
            url: url,
            data: req
        }).then((res) => {
            console.log(res);
        })
    }

    render() {
        return (
            <div className= "Plaid">
                {this.state.accounts.length < 1 && 
                    <PlaidLink
                    token= {this.state.linkToken}
                    onSuccess={this.onSuccess}
                >
                        Connect to bank
                </PlaidLink>
                }
                {this.state.accounts.length > 0 && 
                    <ListAccounts props={this.state}/>
                }
            </div>
        );
    }
}
export default withRouter(LinkBank);
