import React, { Component } from 'react';
import { PlaidLink } from 'react-plaid-link';
import { makeStyles } from '@material-ui/core/styles';
import { withStyles } from "@material-ui/core/styles";
import Card from '@material-ui/core/Card';
import apis from './apis';
import api from './apis';

const styles = () =>({
    root: {
      minWidth: 275,
      maxWidth: 345,
      textAlign: 'center'
    }
});

class App extends Component {

    constructor(props){
        super(props);


        this.state = {
            linkToken:  ''
        };

        const url = '/LinkToken'

        api.get(url)
        .then(res => {
            this.setState({
                linkToken: res.data.link_token
            });
        })

    }

    // Get Transaction Data using the access token received

    transactionData = (accessToken) => {

        const url = '/TransactionDetails'

        const req = {
            access_token : accessToken,
        };

        apis({
            method: 'post',
            url: url,
            data: req
        }).then((res) => {
            console.log(res.data);
        })
    }

    onSuccess = (token, metadata) => {
        console.log(token);

        // Call Token Exchange Endpoint

        const url = '/TokenExchange'

        const publicToken = {
            public_token : token
        };

        apis({
            method: 'post',
            url: url,
            data: publicToken
        }).then((res) => {
            console.log(res.data);

            this.transactionData(res.data.access_token);
        })

        console.log(metadata)
    };

    render() {
        const { classes } = this.props;

        return (
            <div className="App">
                 <Card className={classes.root} variant="outlined">
                    <PlaidLink
                        token= {this.state.linkToken}
                        onSuccess={this.onSuccess}
                        >
                            Connect to bank
                    </PlaidLink>
                </Card>
            </div>
        );
    }
}
export default withStyles(styles)(App);
